import React, { useContext } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; // Import the toastify CSS

import HomeScreen from "./Screens/HomeScreen";
import ProductScreen from "./Screens/ProductScreen";
import Badge from "react-bootstrap/Badge";
import Nav from "react-bootstrap/Nav";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import { Store } from "./Store";
import CartScreen from "./Screens/CartScreen";
import SigninScreen from "./Screens/SignInScreen";
import NavDropdown from "react-bootstrap/NavDropdown";

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { cart, userInfo } = state;

  const signoutHandler = () => {
    ctxDispatch({
      type: "USER_SIGNOUT",
    });
    localStorage.removeItem("userInfo");
  };

  const showToast = () => {
    // Define the content for your toast notification
    const toastContent = (
      <div>
        <p>Hello, this is a custom toast notification.</p>
      </div>
    );

    // Show the toast notification with the custom content
    toast(toastContent);
  };

  return (
    <Router>
      <div className="d-flex flex-column site-container">
        <ToastContainer /> {/* This sets up the toast container */}
        <header>
          <Navbar bg="dark" variant="dark">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand>Phone Shop</Navbar.Brand>
              </LinkContainer>
              <Nav className="me-auto">
                <Link to="/cart" className="nav-link">
                  Cart
                  {cart.cartItems.length > 0 && (
                    <Badge pill bg="danger">
                      {cart.cartItems.reduce((a, c) => a + c.quantity, 0)}
                    </Badge>
                  )}
                </Link>
                <Link to="/" className="nav-link" onClick={showToast}>
                  Show Toast {/* Add a link to trigger the custom toast */}
                </Link>
                {userInfo ? (
                  <NavDropdown title={userInfo.name} id="basic-nav-dropdown">
                    <LinkContainer to="/profile">
                      <Navbar.Brand>Profile</Navbar.Brand>
                    </LinkContainer>
                    <LinkContainer to="/orderhistory">
                      <Navbar.Brand>Order History</Navbar.Brand>
                    </LinkContainer>
                    <Link
                      to="/signout"
                      className="dropdown-item"
                      onClick={signoutHandler}
                    >
                      Sign out
                    </Link>
                  </NavDropdown>
                ) : (
                  <Link to="/signin" className="nav-link">
                    Sign In
                  </Link>
                )}
              </Nav>
            </Container>
          </Navbar>
        </header>
        <main>
          <Container className="mt-3">
            <Routes>
              <Route path="/product/:slug" element={<ProductScreen />} />
              <Route path="/cart" element={<CartScreen />} />
              <Route path="/signin" element={<SigninScreen />} />
              <Route path="/" element={<HomeScreen />} />
            </Routes>
          </Container>
        </main>
        <footer>
          <div className="text-center">All rights reserved @2022</div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
