import React, { useContext,useState } from "react";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Rating from "./Rating";
import axios from "axios";
import { Store } from "../Store";


function Product(props) {
  
  const { product } = props;
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;


  const addToCartHandler = async (item) => {
    const existItem = cartItems.find((x) => x._id === product._id);
    const quantity = existItem ? existItem.quantity + 1 : 1;
    const { data } = await axios.get(`/api/products/${item._id}`);
    if (data.countInStock < quantity) {
      window.alert("Sorry Product is out of stock");
      return;
    }
    ctxDispatch({
      type: "CART_ADD_ITEM",
      payload: { ...item, quantity },
    });
  };
  const ProductCard = ({ product, addToCartHandlers}) => {
    const [clicked, setClicked] = useState(false);
  
    const handleClicks = () => {
      setClicked(true);
      setTimeout(() => {
        setClicked(false);
      }, 300);
    };}
  return (
    /*product cards in Homescreen*/
    <Card  className={`product-card ${clicked ? 'clicked' : ''}`} // Add the 'clicked' class when clicked
    onClick={handleClicks} >
      <Link to={`/product/${product.slug}`}>
        <img src={product.image} alt={product.image} className="card-img-top" />
      </Link>
      <Card.Body>
        <Link to={`/product/${product.slug}`}>
          <Card.Title>{product.name} </Card.Title>
        </Link>
        <Rating
          rating={product.rating}
          numReviews={product.numReviews}
        ></Rating>
        <Card.Text>{product.price} $</Card.Text>
        {product.countInStock === 0 ? (
          <Button variant="light" disabled>
            Out Of Stock
          </Button>
        ) : (
          <Button onClick={() => addToCartHandler(product)}>Add To Cart</Button>
        )}{" "}
      </Card.Body>
    </Card>
  );
}

export default Product;
